# coding=utf-8
import os

from qrcode.image.svg import SvgFragmentImage

from flask_qrcode import QRcode
from .server import start_test_server

basedir = os.path.abspath(os.getcwd())
testdir = os.path.join(basedir, "tests")
TEST_PORT = 5000

start_test_server(TEST_PORT)


def test_load_online_icon_qrcode():
    qrcode = QRcode()
    old_qr = qrcode("Test Data")
    new_qr = qrcode("Test Data", icon_img="http://localhost:5000/tests/icon.jpg")
    assert "data:image/png;base64," in new_qr
    assert len(new_qr) > len(old_qr)


def test_load_local_icon_qrcode():
    qrcode = QRcode()
    old_qr = qrcode("Test Data")
    new_qr = qrcode("Test Data", icon_img=os.path.join(testdir, "icon.jpg"))
    assert "data:image/png;base64," in new_qr
    assert len(new_qr) > len(old_qr)


def test_simple_content_qrcode():
    qrcode = QRcode()
    generated_b64 = qrcode("Do you speak QR?")
    expected_b64 = "data:image/png;base64,"

    assert generated_b64 == expected_b64


def test_box_size_qrcode():
    qrcode = QRcode()
    generated_b64 = qrcode("I'm bigger than you!", box_size=20)
    expected_b64 = "data:image/png;base64,"

    assert generated_b64 == expected_b64


def test_border_qrcode():
    qrcode = QRcode()
    generated_b64 = qrcode("Whatever... can't touch me", border=10)
    expected_b64 = "data:image/png;base64,"

    assert generated_b64 == expected_b64


def test_error_correction_qrcode():
    qrcode = QRcode()
    generated_b64 = qrcode(
        "You can read me even if I'm 30% hurt.", error_correction="H"
    )
    expected_b64 = "data:image/png;base64,"

    assert generated_b64 == expected_b64


def test_combo_qrcode():
    qrcode = QRcode()
    generated_b64 = qrcode("FATALITY", box_size=12, border=5)
    expected_b64 = "data:image/png;base64,"

    assert generated_b64 == expected_b64


def test_color_qrcode():
    qrcode = QRcode()
    generated_b64 = qrcode(
        "You can see green front color and #CCC back color.",
        error_correction="H",
        back_color="ccc",
        fill_color="green",
    )
    expected_b64 = "data:image/png;base64,"

    assert generated_b64 == expected_b64


def test_remote_icon_image_qrcode():
    qrcode = QRcode()
    generated_b64 = qrcode(
        "You can see a icon in QR code.",
        error_correction="H",
        icon_img="http://www.localhost:5000/tests/icon.jpg",
    )
    expected_b64 = "data:image/png;base64,"

    assert generated_b64 == expected_b64


def test_local_icon_image_qrcode():
    qrcode = QRcode()
    generated_b64 = qrcode(
        "Load icon image from static folder.",
        error_correction="H",
        icon_img=os.path.join(testdir, "icon.jpg"),
    )
    expected_b64 = "data:image/png;base64,"

    assert generated_b64 == expected_b64


def test_simple_content_svg_base64_qrcode():
    qrcode = QRcode()
    generated_b64 = qrcode(
        "Do you speak QR?",
        error_correction="M",
        image_factory=SvgFragmentImage,
    )
    expected_b64 = "data:image/svg+xml;base64,"

    assert generated_b64 == expected_b64


